//     }
// }

export async function getMarketNfts(category) {
    const web3 = new Web3(window.ethereum);
    if (typeof web3 !== 'undefined') {
        var abi = JSON.parse(nftTraderAbi);
        var myContract = new web3.eth.Contract(abi, nftTraderContractAddress);
        console.log("getMarketNfts called");
        const response = await myContract.methods.getNFTs(category)
            .call({
                from: window.web3.currentProvider.selectedAddress,
            });
        const result = [];
        console.log("getMarketNfts recieved result:", response);
        for (let i = 0; i < response.length; i++) {
            if (response[i][1]) {
                var category_type;
                if (category == "land") category_type = 0;
                else if (category == "wearable") category_type = 1;
                else if (category == "decoration") category_type = 2;
                else {
                    console.log("category ", category, " not known!");
                    return;
                }
                result.push({
                    price: response[i][2],
                    onSale: response[i][1],
                    owner: response[i][0].toLowerCase(),
                    contractAddress: response[i][5],
                    tokenId: response[i][4],
                    category: category_type
                })
            }
        }
        return result;
    } else {
        console.log("web3 not defined");
    }
}

export async function getUserNfts(public_key, category) {
    const web3 = new Web3(window.ethereum);
    if (typeof web3 !== 'undefined') {
        var abi = JSON.parse(nftTraderAbi);
        var myContract = new web3.eth.Contract(abi, nftTraderContractAddress);

        const response = await myContract.methods.getNFTs(category)
            .call({
                from: window.web3.currentProvider.selectedAddress,
            });
        const result = [];
        for (let i = 0; i < response.length; i++) {
            if (response[i][0].toLowerCase() == public_key) {
                result.push({
                    price: response[i][2],
                    onSale: response[i][1],
                    owner: response[i][0].toLowerCase(),
                    contractAddress: response[i][5],
                    tokenId: response[i][4],
                    category: category
                })
            }
        }
        console.log("result:", result);
        return result;
    } else {
        console.log("web3 not defined");
    }
}

export async function getSpecific(category, contractAddress, id) {
    const web3 = new Web3(window.ethereum);
    if (typeof web3 !== 'undefined') {
        var abi = JSON.parse(nftTraderAbi);
        var myContract = new web3.eth.Contract(abi, nftTraderContractAddress);

        const response = await myContract.methods.getNFTs(category)
            .call({
                from: window.web3.currentProvider.selectedAddress,
            });
        const result = {
            _id: id,
            price: response[id][0],
            owner: response[id][1].toLowerCase(),
            valid: response[id][2]
        }
        console.log("result:", result);
        return result;
    } else {
        console.log("web3 not defined");
    }
}

export async function createProduct(product) {
    return (await fetch(`${baseUrl}/products/create`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(product)
    })).json();
}

export async function editProduct(id, product) {
    return (await fetch(`${baseUrl}/products/edit/${id}`, {
        method: 'PATCH',
        headers: {
            'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(product)
    })).json();
}


export async function activateSell(id) {
    return (await fetch(`/products/enable/${id}`)).json()
}

export async function archiveSell(id) {
    return (await fetch(`/products/archive/${id}`)).json()
}

export async function wishProduct(id) {
    return (await fetch(`${baseUrl}/products/wish/${id}`, { credentials: 'include' })).json();
}
