import { useContext, useEffect } from 'react';
import { Context } from '../../ContextStore';
import { Navbar, NavDropdown, Nav, OverlayTrigger, Tooltip } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { BsFillPersonFill, BsFillEnvelopeFill, BsFillPlusCircleFill } from 'react-icons/bs';
import { IoLogOut } from 'react-icons/io5'
import { connect, getAddress, checkIfWalletIsConnected } from '../../services/userData'
import './Header.css'
// import useMetaMask from '../../hooks/metamask';


function Header() {
    // const { connect, disconnect, isActive, account, shouldDisable } = useMetaMask()
    const { userData, setUserData } = useContext(Context)

    useEffect(() => {
        checkIfWalletIsConnected(setUserData);
    }, []);

    // var address = getAddress();

    console.log("header se bhai", userData);
    const avatar_pic = "https://www.google.com/search?q=profile+pic&sxsrf=APq-WBsGFnbBycd0kkTeyXx5ZJ4Vd60lbA:1647768441788&tbm=isch&source=iu&ictx=1&vet=1&fir=7lQrPxjSv8FjeM%252CFvQHUVZ-cx81xM%252C_%253BH6pHpB03ZEAgeM%252Cwg0CyFWNfK7o5M%252C_%253ByRz0asXqc1iKxM%252Cb5C9ViMmmhpq-M%252C_%253Bms04c54Z3JThMM%252CTllTuaA6cS7N9M%252C_%253BZTBVWw5bGIMnsM%252Cb5C9ViMmmhpq-M%252C_%253B8WoEgpJPeH0e2M%252CFvQHUVZ-cx81xM%252C_%253BX6p_je86_WozdM%252CZgjn3YxIJzIrrM%252C_%253BWgxrqZ8j04F8BM%252Cw0rekurKV2OhcM%252C_%253B8z0GwDReiUhJ6M%252CMS0735Gr0ZdLzM%252C_%253B-H_ExttvR7ahYM%252Cb5C9ViMmmhpq-M%252C_%253BeHQKa74ZnnpTfM%252CSixlWtBpRVa7SM%252C_%253BmJpvnGPmGXOMkM%252CtSXubikre3IIEM%252C_%253BvIcuce5Lolvf5M%252C7X2aM4E4dK2D2M%252C_%253BFlbGF9bWzT_KqM%252CMS0735Gr0ZdLzM%252C_%253B6RmIrzFFfd0sLM%252CNWVUfzuKBXde5M%252C_%253BnyFMOkQaK3q9eM%252CDFlrvMtp2WJ07M%252C_%253BtGxL9Am-vIdLoM%252CF42U8RH5-kZpwM%252C_&usg=AI4_-kRM82aDjHTyEotf3z-2ai5cOi4hig&sa=X&ved=2ahUKEwiV-pPlr9T2AhWC3zgGHXr5CgkQ9QF6BAgWEAE#imgrc=H6pHpB03ZEAgeM";
    return (
        <Navbar collapseOnSelect bg="light" variant="light">
            <div className="container">
                <Navbar.Brand>
                    <NavLink className="navbar-brand" to="/">Volaverse</NavLink>
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className="mr-auto">
                        {/* <Nav.Link href="#features">Features</Nav.Link>
                        <Nav.Link href="#pricing">Pricing</Nav.Link> */}
                    </Nav>
                    {userData ?
                        (<Nav>
                            <NavLink className="nav-item" id="addButton" to="/add-product">
                                <OverlayTrigger key="bottom" placement="bottom"
                                    overlay={
                                        <Tooltip id={`tooltip-bottom`}>
                                            <strong>Add</strong>  a sell.
                                        </Tooltip>
                                    }
                                > 
                                    <BsFillPlusCircleFill />
                                </OverlayTrigger>
                            </NavLink>

                            <NavDropdown title={userData.substr(0,6) + "..."} drop="left" id="collasible-nav-dropdown">
                                <NavLink className="dropdown-item" to={`/profile/${userData}`}>
                                    <BsFillPersonFill />Profile
                                </NavLink>

                                {/* <NavDropdown.Divider /> */}

                                {/* <NavLink className="dropdown-item" to="/your-sells">
                                    <BsFillGridFill />Sells
                            </NavLink> */}
                                <NavLink className="dropdown-item" to="/messages">
                                    <BsFillEnvelopeFill />Messages
                            </NavLink>
                                {/* <NavLink className="dropdown-item" to="/wishlist">
                                    <BsFillHeartFill />Wishlist
                            </NavLink> */}

                                <NavDropdown.Divider />

                                {/* <NavLink className="dropdown-item" to="/auth/logout" onClick={() => {
                                    setUserData(null)
                                }}>
                                    <IoLogOut />Log out
                                </NavLink> */}
                            </NavDropdown>
                        </Nav>)
                        :
                        (<Nav>
                            <NavLink className="nav-item" id="nav-sign-in" to="/auth/login">
                                Login
                            </NavLink>
                            {/* <NavLink className="nav-item" id="nav-sign-up" to="/auth/register">
                                Sign Up
                            </NavLink> */}
                        </Nav>)
                    }
                </Navbar.Collapse>
            </div>
        </Navbar>
    )
}

export default Header;
