import { Card } from 'react-bootstrap';
// import { BsHeart, BsHeartFill } from 'react-icons/bs';
import { Link } from 'react-router-dom';
import Moment from 'react-moment';
import land_img from '../../assets/land.png';
import decoration_img from '../../assets/decoration.jpg';
import wearable_img from '../../assets/wearable.jpg';

function ProductCard({ params }) {
    return (
        <Card style={{ backgroundColor: '#E7E6E6'}} text='#873AC0' >
            <Link to={`/nft/${params.contractAddress}/${params.tokenId}/details`}>
                {params.category == "land" && <Card.Img variant="top" src={land_img} />}
                {params.category == "wearable" && <Card.Img variant="top" src={wearable_img} />}
                {params.category == "decoration" && <Card.Img variant="top" src={decoration_img} />}
                <Card.Body>
                    <Card.Title>{params.contractAddress.substr(0,6) + "..."}</Card.Title>
                    <Card.Text>token ID : #{params.tokenId}</Card.Text>
                    {params.onSale && <p className="cardBody" color='#873AC0'>On Sale </p>}
                    {params.onSale && <Card.Text color='#873AC0'>{params.price} weth</Card.Text>}
                    {!params.onSale && <Card.Text color='#873AC0'>Not On Sale </Card.Text>}
                </Card.Body>
            </Link>
            {/* <Card.Footer>
                <small className="text-muted">
                    <Moment format="d MMM YYYY (dddd) HH:mm">
                        {"0 000 0000 (0000) 00;00"}
                    </Moment>
                </small>
            </Card.Footer> */}
        </Card>
    )
}

export default ProductCard;
