import { useState, useEffect } from 'react';
import { Row, Tabs, Tab, Image, OverlayTrigger , Tooltip} from 'react-bootstrap';
import { BsHeart, BsHeartFill } from 'react-icons/bs';
import { wishProduct } from '../../../services/productData'

function ProductInfo({ params }) {
    const [wish, setWish] = useState(false);

    const [landId, setLandId] = useState(-1);

    useEffect(() => {
        if (params.isWished === true) {
            setWish(true)
        } else {
            setWish(false)
        }
    }, [params.isWished, setWish])

    useEffect(() => {
        setLandId(params._id);
    }, [params._id, setLandId])

    const onHearthClick = () => {
        if (wish === false) {
            wishProduct(params._id)
                .then(res => {
                    setWish(true);
                })
                .catch(err => console.log(err))
        } else {
            wishProduct(params._id)
                .then(res => {
                    setWish(false);
                })
                .catch(err => console.log(err))
        }
    }
    const LandGroup = (landParams) => {
        const lands = [];
        for (let i=0;i<4;i++) {
            for (let j=0;j<2;j++) {
                const ind = landParams.start + i*10 + j;
                console.log(ind, landId);
                if (ind === landId) lands.push(<div class="selected-land">{ind}</div>);
                else lands.push(<div class="land">{ind}</div>);
            }
        }
        return <div class="land-group">
        {lands}
        </div>;
    }
    const MyMap = () => {
        const groups = [];
        console.log(landId);
        for (let i=0;i<15;i++) {
            const row1 = i / 5;
            const col1 = i % 5;
            groups.push(<LandGroup start={row1 * 10 + col1} />);
        }
        return <div class="grid-container">
            {groups}
        </div>
    };
    return (
        <>
            {/* <Image className="col-lg-12" src={params.image} rounded /> */}
            <MyMap />
            <Row>
                <h1 className="col-lg-10 col-sm-10 product-info-heading">{"Land: " + params._id}</h1>
                {/* <span id="heartIconDetails" className="col-lg-1 col-sm-1" onClick={onHearthClick}> */}
                {/* {params.isAuth && <>
                    {!wish ? (
                        <OverlayTrigger placement="top" overlay={<Tooltip>Add to Wishlist</Tooltip>}>
                            <BsHeart />
                        </OverlayTrigger>
                    )
                        : (
                            <OverlayTrigger placement="top" overlay={<Tooltip>Remove from Wishlist</Tooltip>}>
                                <BsHeartFill />
                            </OverlayTrigger>
                        )
                    }
                </>} */}

                {/* </span> */}
            </Row>
            <div id="detailsCardText" className="col-lg-12">
                <Tabs defaultActiveKey="details" transition={false}>
                    <Tab eventKey="details" title="Details" id="tab-details">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                        <hr />
                        <p id="details-footer" className="text-muted">Product listed at {"00-00-0000"}</p>
                    </Tab>
                    {/* <Tab eventKey="aboutSeller" title="About seller">
                        <p>Name: {params.name || "Not specified"}</p>
                        <p>Email: {params.email}</p>
                        <p>Telephone: {params.phone}</p>
                        <p>City: {params.city}</p>
                    </Tab> */}
                </Tabs>
            </div>
        </>
    )
}

export default ProductInfo;
