import { useState, useEffect } from 'react';
import { Row, Tabs, Tab, Image, OverlayTrigger , Tooltip} from 'react-bootstrap';
import ReactThreeFbxViewer from 'react-three-fbx-viewer';
// let fbxUrl = '../../../assets/FirstDecorationTable.fbx';
// import fbxUrl from '../../../assets/cerca2.fbx';
// import ThreeScene from '../../ThreeScene/ThreeScene';
import fbxUrl from '../../../assets/FirstDecorationTable.fbx';

function ProductInfo({ params }) {
    const [info, setInfo] = useState({
        category: "loading",
        contractAddress: "loading",
        tokenId: -1
    });

    useEffect(() => {
        if (params.category && params.contractAddress && params.tokenId)
        setInfo({
            category: params.category,
            contractAddress: params.contractAddress,
            tokenId: params.tokenId
        });
    }, [params, setInfo])

    const LandGroup = (landParams) => {
        const lands = [];
        for (let i=0;i<4;i++) {
            for (let j=0;j<2;j++) {
                const ind = landParams.start + i*10 + j;
                if (ind == info.tokenId) lands.push(<div className="selected-land">{ind}</div>);
                else lands.push(<div className="land" key={"land" + ind}>{ind}</div>);
            }
        }
        return <div className="land-group">
        {lands}
        </div>;
    }
    const MyMap = () => {
        const groups = [];
        for (let i=0;i<15;i++) {
            const row1 = Math.floor(i / 5);
            const col1 = i % 5;
            groups.push(<LandGroup start={row1 * 40 + col1 * 2} key={i} />);
        }
        return <div className="grid-container">
            {groups}
        </div>
    };
    console.log("Info:",info);
    console.log("url:", fbxUrl);
    // onLoad(e) {
    //     console.log(e);
    // }
    
    // onError(e) {
    //     console.log(e);
    // }
    let cameraPosition = {
        x:300,
        y:600,
        z:700 
      }
    return (
        <>
            {/* <Image className="col-lg-12" src={params.image} rounded /> */}
            {/* <div style="width:250px; max-width:250px; display: inline-block;"> */}
            <div style={{width:"250px", max_width:"250px", display: "inline-block"}}>
            {/* <div> */}
                {params.category == "land" && <MyMap />}
                {params.category == "decoration" && <ReactThreeFbxViewer style={{transform:"scale(0.1)"}} cameraPosition={cameraPosition} url={fbxUrl} />}

            </div>
            <Row>
                <h1 className="col-lg-10 col-sm-10 product-info-heading">{info.category}</h1>
                <h6 className="col-lg-10 col-sm-10">{"Address: " + info.contractAddress.substr(0,6)+"..."}</h6>
                <h6 className="col-lg-10 col-sm-10">{"Token ID: " + info.tokenId}</h6>
            </Row>
            <div id="detailsCardText" className="col-lg-12">
                <Tabs defaultActiveKey="details" transition={false}>
                    <Tab eventKey="details" title="Details" id="tab-details">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                        <hr />
                        <p id="details-footer" className="text-muted">Product listed at {"00-00-0000"}</p>
                    </Tab>
                </Tabs>
            </div>
        </>
    )
}

export default ProductInfo;
