export async function getLands() {
    const web3 = new Web3(window.ethereum);
    if (typeof web3 !== 'undefined') {
        var abi = JSON.parse(nftTraderAbi);
        var myContract = new web3.eth.Contract(abi, nftTraderContractAddress);

        const response = await myContract.methods.getListings(landContractAddress, 120)
            .call({
                from: window.web3.currentProvider.selectedAddress,
            });
        const result = [];
        for (let i = 0; i < response.length; i++) {
            if (response[i][2]) {
                result.push({
                    _id: i,
                    price: response[i][0],
                    owner: response[i][1].toLowerCase()
                })
            }
        }
        return result;
    } else {
        console.log("web3 not defined");
    }
}

